import React, { Component } from 'react'
import ContentList from '../component/contentList/ContentList.js';

export default class Search extends Component{
    state = {
        keyword:null,
        contents: []
    };

    handleChange = (e) =>{
        this.setState({
            keyword: e.target.value
        });
        e.preventDefault();
    };

    handleSubmit = (e) =>{
        this.fetchSearch(this.state.keyword);
        e.preventDefault();
    };

    fetchSearch = (keyword) =>{
