import React,{Component} from 'react'
import ContentList from '../component/contentList/ContentList';

import axios from "axios";


export default class Search extends Component {

    state= {
        keyword: null,  
        contents:[] 
    }

    handleChange = (e) =>{
        this.setState({
            keyword: e.target.value
        }); 
        e.preventDefault();
    }  

    handleSubmit = (e) => {
        e.preventDefault();
    }

    fetchSearch = async keyword =>{ 
        // 유투브로부터 데이터를 받아오는 함수임
        let maxResults = 30
        let token = 'alX'

        // axios
        try {   
            let result = await axios.get('https://www.googleapis.com/youtube/v3/search?q='+keyword+'&part=snippet&key='+token+'&maxResults='+maxResults);
        } catch{

        }

    } 

    render() {
        const {keyword, contents} = this.state;
        const {handleChange} = this;  

        return (

            <> 
                <div> 
                    <form onSubmit={this.handleSubmit} > 
                        <input onChange={this.handleChange} name="keyword" type="text"  /> 

                        <input type="submit" />  
                    </form>

                    <div classname="content">
                    <ContentList contents={contents} />

                    </div>
                </div> 
            </>
        );
    }
}
