import React, { Component } from 'react'

export default class Search extends Component {
constructor(props){
    super(props);
    this.state = {
        keyword : "",
        contents : ""
    };

}
handleInputChange = e => {
    this.setState({keyword:e.target.value});
}
  render() {  

    return (
      <div>
          <div>
              <form>
					<label>검색 : </label>
                    <input 
                    type="text" 
                    value={this.state.keyword}
                    onChange={this.handleInputChange}
                    />
              </form>
          </div>

      </div>
    )
  }
}
